INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341710, 'Digoxin', 'Metformin', 'Moderate', 'Digoxin is a cationic drug and theoretically could decrease the excretion of metformin by competing for renal tubular transport. In addition, patients who are being pharmacologically treated for congestive heart failure appear to be at greater risk of developing lactic acidosis.', 'DDInter', 1767317475, 'If digoxin and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended. The maximal dose of metformin probably also should be reduced until further information about this interaction is available. Patients should be advised to promptly notify their physician if they experience malaise, myalgia, respiratory distress, hyperventilation, slow or irregular heartbeat, somnolence, abdominal upset, or other unusual symptoms.', 'Excretion', 'If digoxin and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
