INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341718, 'Erdafitinib', 'Metformin', 'Moderate', 'Coadministration with erdafitinib may increase the plasma concentrations of drugs that are substrates of organic cation transporter 2 (OCT2), resulting in increased toxicity of these drugs. In vitro, erdafitinib has been shown to be an inhibitor of OCT2. However, no pharmacokinetic data are available from drug interaction studies.', 'DDInter', 1767317475, 'Concomitant use of erdafitinib and OCT2 substrates should generally be avoided or the dose of the OCT2 substrates should be reduced based on tolerability.', 'Excretion', 'Concomitant use of erdafitinib and OCT2 substrates should generally be avoided or the dose of the OCT2 substrates should be reduced based on tolerability.', NULL, NULL, 'Moderate', 'pharmacodynamic');
