INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341734, 'Furosemide', 'Metformin', 'Moderate', 'One study has suggested that furosemide may increase plasma concentrations of metformin by 22% (without changes in metformin clearance) and that metformin may decrease the peak concentration and elimination half-life of furosemide by 31% and 32%, respectively. The clinical implications of these findings are uncertain. Increased metformin levels may increase the risk of lactic acidosis.', 'DDInter', 1767317475, 'If furosemide and metformin must be used together, it is recommended that the clinician observe closely for evidence that the effects of either drug have been altered. Patients should be advised to monitor their blood glucose and to promptly notify their physician if they experience possible signs of lactic acidosis such as malaise, myalgia, respiratory distress, hyperventilation, slow or irregular heartbeat, somnolence, abdominal upset, or other unusual symptoms.', 'Others', 'If furosemide and metformin must be used together, it is recommended that the clinician observe closely for evidence that the effects of either drug have been altered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
