INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341766, 'Iodipamide', 'Metformin', 'Major', 'Administration of intravascular iodinated radiocontrast media in patients treated with biguanides such as metformin may precipitate lactic acidosis, a rare but potentially serious and fatal complication of biguanide therapy. Iodinated contrast material can cause acute alterations in renal function, including acute renal failure, which is a known risk factor for biguanide-induced lactic acidosis.', 'DDInter', 1767317475, 'Biguanide therapy should be temporarily discontinued at the time of, or prior to, radiographic studies involving intravascular administration of iodinated contrast media, and not resumed until at least 48 hours after the procedure and renal function has been re-evaluated and determined to be normal.', 'Excretion', 'Biguanide therapy should be temporarily discontinued at the time of, or prior to, radiographic studies involving intravascular administration of iodinated contrast media, and not resumed until at least 48 hours after the procedure and renal function has been re-evaluated and determined to be normal.', NULL, NULL, 'Major', 'pharmacodynamic');
