INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341872, 'Tafenoquine', 'Metformin', 'Moderate', 'Coadministration with tafenoquine may increase the plasma concentrations of drugs that are substrates of organic cation transporter-2 (OCT2) or multidrug and toxin extrusion (MATE) transporters. Clinical drug interaction studies with tafenoquine and OCT2 and MATE substrates have not been conducted in humans. However, in vitro observations suggest the potential for increased concentrations of these substrates, which may result in increased toxicities. Specifically, tafenoquine inhibited metformin transport via human OCT2, MATE-1, and MATE2-K transporters in vitro.', 'DDInter', 1767317475, 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided. If coadministration is required, patients should be monitored for drug-related toxicities and dosage reductions considered as needed in accordance with the product labeling of the coadministered drugs.', 'Excretion', 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
