INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341900, 'Warfarin', 'Metformin', 'Moderate', 'Coadministration with metformin may decrease the anticoagulant effects of vitamin K antagonists (VKAs). The mechanism has not been delineated, but it may involve metformin-mediated increase in the elimination rate of VKAs. In addition, concomitant use of VKAs with metformin may increase the risk of severe hypoglycemia. The clinical significance of this interaction is unknown.', 'DDInter', 1767317475, 'Until more information is available, patients receiving VKAs with metformin should be closely monitored during concomitant therapy. The INR or prothrombin time should be checked frequently and the VKA dosage adjusted accordingly, particularly following initiation, change of dosage, or discontinuation of metformin therapy. In addition, patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs.', 'Metabolism', 'Until more information is available, patients receiving VKAs with metformin should be closely monitored during concomitant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
