INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30175, 'Bosentan', 'Aripiprazole', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aripiprazole, which is partially metabolized by the isoenzyme.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary. The manufacturer recommends doubling the aripiprazole dosage when a potent CYP450 3A4 inducer is added to therapy, and additional dosage adjustments made based on clinical evaluation. Potent inducers include carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with aripiprazole is unknown. The aripiprazole dosage should be reduced following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, More', 'Fluphenazine, Haloperidol, Thiothixene, Amisulpride, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, Methotrimeprazine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, '