INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30238, 'Crizotinib', 'Aripiprazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of aripiprazole, which is primarily metabolized by these isoenzymes.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 and/or 2D6 inhibitor is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 and/or 2D6 inhibitor is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary. The manufacturer recommends that aripiprazole dosage be reduced to one-half the normal dosage during concomitant administration with ketoconazole or quinidine, and additional dosage adjustments be made based on clinical evaluation.', 'Metabolism', 'Loxapine, Molindone, Thiothixene', 'Acalabrutinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Axitinib, Ripretinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30291/', '[1] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30239, 'Cyclandelate', 'Aripiprazole', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Droperidol, Pimozide, Pimavanserin', 'Pentoxifylline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30292/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30240, 'Cyclizine', 'Aripiprazole', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Azelastine, Ketotifen', 'Methotrimeprazine, Promazine, Pimavanserin, Amisulpride, Triflupromazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30293/', '[1] Kulik AV, Wilbur R "Delirium and stereotypy from anticholinergic antiparkinson drugs." Prog Neuropsychopharmacol Biol Psychiatry 6 (1982): 75-82[2] Mann SC, Boger WP "Psychotropic drugs, summer heat and humidity, and hyperplexia: a danger restated." Am J Psychiatry 135 (1978): 1097-100[3] Warnes H, Lehmann HE, Ban TA "Adynamic ileus during psychoactive medication: a report of three fatal and five severe cases." Can Med Assoc J 96 (1967): 1112-3[4] Sarnquist F, Larson CP Jr "Drug-induced heat stroke." Anesthesiology 39 (1973): 348-50[5] Gershon S, Neubauer H, Sundland DM "Interaction between some anticholinergic agents and phenothiazines." Clin Pharmacol Ther 6 (1965): 749-56[6] Lee BS "Possibility of hyperpyrexia with antipsychotic and anticholinergic drugs." J Clin Psychiatry 47 (1986): 571[7] Zelman S, Guillan R "Heat stroke in phenothiazine-treated patients: a report of three fatalities." Am J Psychiatry 126 (1970): 1787-90[8] Cohen MA, Alfonso CA, Mosquera M "Development of urinary retention during treatment with clozapine and meclizine [published erratum appears in Am J Psychiatry 1994 Jun;151(6):952]." Am J Psychiatry 151 (1994): 619-20[9] Moreau A, Jones BD, Banno V "Chronic central anticholinergic toxicity in manic depressive illness mimicking dementia." Can J Psychiatry 31 (1986): 339-41[10] "Product Information. Artane (trihexyphenidyl)." Lederle Laboratories, Wayne, NJ.[11] Hvizdos AJ, Bennett JA, Wells BG, Rappaport KB, Mendel SA "Anticholinergic psychosis in a patient receiving usual doses of haloperidol." Clin Pharm 2 (1983): 174-8[12] Stadnyk AN, Glezos JD "Drug-induced heat stroke." Can Med Assoc J 128 (1983): 957-9[1