INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30256, 'Delavirdine', 'Aripiprazole', 'Moderate', 'Coadministration with ketoconazole or other potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of aripiprazole, which is partially metabolized by the isoenzyme.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever ketoconazole or other potent inhibitors of CYP450 3A4 (e.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever ketoconazole or other potent inhibitors of CYP450 3A4 (e.g., itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, ketolide and certain macrolide antibiotics) are added to or withdrawn from therapy. The manufacturer recommends that aripiprazole dosage be reduced to one-half the normal dosage during concomitant administration with ketoconazole, and additional dosage adjustments be made based on clinical evaluation. Although clinical data are lacking, similar dosage adjustments may be appropriate during coadministration with other potent CYP450 3A4 inhibitors. Aripiprazole dosage should be increased accordingly if these agents are discontinued.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30309/', '[1] "Product Information. Abilify (ar