INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30496, 'Nadolol', 'Aripiprazole', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Droperidol, Pimozide, Pimavanserin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30549/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30497, 'Nafcillin', 'Aripiprazole', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aripiprazole, which is partially metabolized by the isoenzyme.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary.', NULL, 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary. The manufacturer recommends doubling the aripiprazole dosage when a potent CYP450 3A4 inducer is added to therapy, and additional dosage adjustments made based on clinical evaluation. Potent inducers include carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with aripiprazole is unknown. The aripiprazole dosage should be reduced following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Sulbactam, Oxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, Benzylpenicillin, More', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Pimozide, Trifluoperazine, Paliperidone, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://