INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (30824, 'Dinutuximab', 'Triamcinolone', 'Major', 'Coadministration of dinutuximab or dinutuximab beta with corticosteroids may potentiate the risk of infections due to additive immunosuppressive activity. Infections, including sepsis, pneumonia, herpes virus infection, myelitis, and encephalomyelitis as well as hematologic toxicities, including thrombocytopenia and neutropenia, have been reported with both dinutuximab and dinutuximab beta therapy.', NULL, 'The manufacturer of dinutuximab beta recommends that, except for life threatening conditions, concomitant use of dinutuximab beta and corticosteroids should be avoided for 2 weeks prior to the first course of dinutuximab beta and for 1 week after the last course (UK).', NULL, 'The manufacturer of dinutuximab beta recommends that, except for life threatening conditions, concomitant use of dinutuximab beta and corticosteroids should be avoided for 2 weeks prior to the first course of dinutuximab beta and for 1 week after the last course (UK). If concomitant use is required, close clinical and laboratory monitoring for the development of systemic infections and severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30877/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365203);
