INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62746, 'Clidinium', 'Arbutamine', 'Moderate', 'Atropine and other agents with significant anticholinergic activity (e.g., clozapine) may enhance the chronotropic response to arbutamine due to their blockade of normal vagal inhibition of the sinoatrial node, resulting in increased sinoatrial node automaticity.', NULL, 'As the dosing of arbutamine is based on the heart rate response of the patient, the concomitant use of atropine or other anticholinergic agents should be avoided.', NULL, 'As the dosing of arbutamine is based on the heart rate response of the patient, the concomitant use of atropine or other anticholinergic agents should be avoided.', 'Synergism', 'Dobutamine, Norepinephrine, Midodrine, Amrinone, Droxidopa, Angiotensin II, Ephedrine, Milrinone, Fenoldopam, Isoprenaline, Metaraminol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62832/', '[1] "Product Information. GenESA (arbutamine)." Gensia Inc, San Diego, CA.', 1767369485);
