INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62758, 'Echothiophate (ophthalmic)', 'Clidinium', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62844/', '[1] "Product Information. Pilopine HS (pilocarpine ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Multum Information Services, Inc. Expert Review Panel"', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62759, 'Edrophonium', 'Clidinium', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', 'Sorbitol, Caffeine, Indocyanine green, Tolbutamide, Inulin, Hexaminolevulinate, Galactose, Corticorelin, Macimorelin, Methacholine, Folic acid, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62845/', '[1] "Product Information. Mestinon (pyridostigmine)." ICN Pharmaceuticals Inc, Cost Mesa, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62760, 'Eluxadoline', 'Clidinium', 'Moderate', 'The risk of constipation and serious constipation-related adverse reactions may be increased when eluxadoline is used with other drugs that are also associated with this adverse effect.', NULL, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.', NULL, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided. Loperamide may be used occasionally for acute management of severe diarrhea, but chronic use is not recommended, and it should be discontinued immediately if constipation occurs. Eluxadoline should also be discontinued if constipation occurs for more than 4 days.', 'Synergism', 'Diphenoxylate, Difenoxin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62846/', '[1] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.', 1767369485);
