INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62846, 'Chloroquine', 'Boceprevir', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Artesunate, Mefloquine, Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62932/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62847, 'Chloroquine', 'Calcium carbonate', 'Moderate', 'INTERVAL: Concomitant administration of antacids or kaolin may reduce the oral bioavailability of chloroquine. The proposed mechanism is adsorption of chloroquine by polyvalent cations present in these agents. Delayed dissolution of chloroquine tablets in the presence of antacids may also be a contributing factor.', NULL, 'Chloroquine product labeling suggests separating the doses by at least 4 hours if concomitant therapy with antacids or kaolin is required.', NULL, 'Chloroquine product labeling suggests separating the doses by at least 4 hours if concomitant therapy with antacids or kaolin is required. The same precaution may be applicable to hydroxychloroquine, although no data are available to support this recommendation. Some authorities recommend separating the dose of hydroxychloroquine from antacids and kaolin by at least 2 hours (AU).', 'Absorption', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Sulfadoxine', 'Calcium glucoheptonate, Calcium lactate, Calcium chloride, Calcium gluconate, Calcium glubionate anhydrous, Calcium citrate, Calcium Phosphate, Ascorbic acid, Calcium chloride, Calcium citrate, Calcium acetate, Calcium Phosphate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62933/', '[1] McElnay JC, Mukhtar HA, Arcy PF, Temple DJ "In vitro experiments on chloroquine and pyrimethamine absorption in the presence of antacid constituents or kaolin." J Trop Med Hyg 85 (1982): 153-8[2] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Int