INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62872, 'Dapsone (topical)', 'Chloroquine', 'Moderate', 'Coadministration of oral or topical dapsone with other agents associated with methemoglobinemia may have additive effects and theoretically increase the risk of drug-induced methemoglobinemia. Oral and topical dapsone have been associated with methemoglobinemia, and the risk is increased in patients with glucose-6-phosphate dehydrogenase (G6PD) deficiency or congenital or idiopathic methemoglobinemia.', NULL, 'Concurrent use of oral or topical dapsone with other methemoglobinemia-inducing medications should be followed with monitoring for signs and symptoms of methemoglobinemia (e.', NULL, 'Concurrent use of oral or topical dapsone with other methemoglobinemia-inducing medications should be followed with monitoring for signs and symptoms of methemoglobinemia (e.g., cyanosis, hypoxia).', 'Synergism', 'Minocycline, Clindamycin, Vitamin A, Isotretinoin, Dexamethasone, Fluorometholone, Tretinoin, Methylprednisolone, Azelaic acid, Adapalene', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Atovaquone, Hydroxychloroquine, Halofantrine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62958/', '[1] "Product Information. Aczone (dapsone topical)." QLT USA, Inc, Fort Collins, CO.[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
