INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342303, 'Estradiol', 'Nefazodone', 'Moderate', 'The coadministration with nefazodone may increase the plasma concentrations and pharmacologic effects of ethinyl estradiol and other estrogens. The mechanism is nefazodone inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of estrogens.', 'DDInter', 1767317475, 'Clinicians and patients should be aware of the potential for increased pharmacologic effects of estrogens during concomitant administration with nefazodone. Adjustments in the estrogen dosage may be required if an interaction is suspected. Alternatively, antidepressants that do not significantly interfere with CYP450 3A4 metabolism may be considered, including the SSRIs (except fluvoxamine) and venlafaxine.', 'Metabolism', 'Clinicians and patients should be aware of the potential for increased pharmacologic effects of estrogens during concomitant administration with nefazodone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342304, 'Estradiol', 'Nelfinavir', 'Moderate', 'Coadministration of conjugated estrogens with ritonavir or nelfinavir may lead to decreased plasma concentrations of conjugated estrogens. The proposed mechanism may involve induction of the CYP450 3A4-mediated metabolism of conjugated estrogens by ritonavir and nelfinavir. Increased metabolism of estrogens may lead to decreased efficacy of the hormone replacement therapy. Ritonavir is a known CYP450 3A4 substrate and inhibitor; however, it has also been shown to reduce plasma concentrations of contraceptive hormones via a possible mechanism involving induction of glucuronosyltransferase and/or CYP450 hydroxylation. In addition, along with being a strong CYP450 3A4 inhibitor, nelfinavir has also been shown to possess CYP450 3A4-inducing properties.', 'DDInter', 1767317475, 'Dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever ritonavir or nelfinavir is added to or withdrawn from therapy with conjugated estrogens.', 'Metabolism', 'Dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever ritonavir or nelfinavir is added to or withdrawn from therapy with conjugated estrogens.', NULL, NULL, 'Moderate', 'pharmacodynamic');
