INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342316, 'Estradiol', 'Oxtriphylline', 'Moderate', 'The coadministration with contraceptive steroids may increase the plasma concentrations of theophylline and other methylxanthines. The proposed mechanism is inhibition of methylxanthine metabolism via hepatic microsomal enzymes, of which estrogens and progestins are also substrates.', 'DDInter', 1767317475, 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives. Serum theophylline levels and pharmacologic response should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their theophylline regimen. Patients should be advised to notify their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'Metabolism', 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
