INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342336, 'Estradiol', 'Protriptyline', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 1767317475, 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342337, 'Estradiol', 'Raloxifene', 'Moderate', 'The concurrent use of raloxifene and systemic estrogen or hormone replacement therapy has not been evaluated in prospective clinical trials. Safety and efficacy of this combination are unknown.', 'DDInter', 1767317475, 'Due to the lack of clinical data, use of raloxifene with systemic estrogens is not recommended.', 'Others', 'Due to the lack of clinical data, use of raloxifene with systemic estrogens is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
