INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342368, 'Estradiol', 'Tacrolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', 'DDInter', 1767317475, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342369, 'Estradiol', 'Tazemetostat', 'Moderate', 'Coadministration with tazemetostat may decrease the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to induction of CYP450 3A4 by tazemetostat.', 'DDInter', 1767317475, 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dose adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tazemetostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
