INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342371, 'Estradiol', 'Telaprevir', 'Moderate', 'Coadministration with telaprevir may increase the plasma concentrations of drugs that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telaprevir.', 'DDInter', 1767317475, 'Caution is advised if telaprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telaprevir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if telaprevir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
