INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342451, 'Alpelisib', 'Acetohexamide', 'Moderate', 'Coadministration with alpelisib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 2C9, such as warfarin. The proposed mechanism, based on in vitro data, is increased clearance due to alpelisib-mediated induction of CYP450 2C9.', 'DDInter', 1767317475, 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when alpelisib is prescribed in combination with drugs that are CYP450 2C9 substrates. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever alpelisib is added to or withdrawn from therapy with these drugs. An alternative agent with no or minimal CYP450 2C9-inducing activity may also be considered.', 'Metabolism', 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
