INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342471, 'Alpelisib', 'Canagliflozin', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342472, 'Alpelisib', 'Carbamazepine', 'Major', 'Coadministration of alpelisib with potent inducers of CYP450 3A4 may significantly reduce the plasma concentrations of alpelisib. The proposed mechanism, based on in vitro data, is increased clearance due to induction of CYP450 3A4, which is one of the routes of metabolism of alpelisib.', 'DDInter', 1767317475, 'According to the manufacturer, concomitant use of alpelisib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered.', 'Metabolism', 'According to the manufacturer, concomitant use of alpelisib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342473, 'Alpelisib', 'Carvedilol', 'Moderate', 'Coadministration with alpelisib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 2C9, such as warfarin. The proposed mechanism, based on in vitro data, is increased clearance due to alpelisib-mediated induction of CYP450 2C9.', 'DDInter', 1767317475, 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when alpelisib is prescribed in combination with drugs that are CYP450 2C9 substrates. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever alpelisib is added to or withdrawn from therapy with these drugs. An alternative agent with no or minimal CYP450 2C9-inducing activity may also be considered.', 'Metabolism', 'Patient monitoring is recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
