INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1342565, 'Alpelisib', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of larotrectinib.', 'DDInter', 1767317475, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors. Patients should be monitored for adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paraesthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases), and the larotrectinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
