INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31501, 'Bumetanide', 'Arsenic trioxide', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of arsenic trioxide with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', NULL, 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31554/', '[1] Ohnishi K, Yoshida H, Shigeno K, et al. "Prolongation of the QT interval and ventricular tachycardia in patients treated with arsenic trioxide for acute promyelocytic leukemia." Ann Intern Med 133 (2000): 881-5[2] "Product Information. Trisenox (arsenic trioxide)" Cephalon Inc, West Chester, PA.', 1767365203);
