INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31570, 'Hydroflumethiazide', 'Arsenic trioxide', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of arsenic trioxide with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', NULL, 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31623/', '[1] Ohnishi K, Yoshida H, Shigeno K, et al. "Prolongation of the QT interval and ventricular tachycardia in patients treated with arsenic trioxide for acute promyelocytic leukemia." Ann Intern Med 133 (2000): 881-5[2] "Product Information. Trisenox (arsenic trioxide)" Cephalon Inc, West Chester, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31571, 'Hydroxocobalamin', 'Arsenic trioxide', 'Moderate', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Theoretically, use of arsenic trioxide during intensive vitamin B12 therapy for the treatment of megaloblastic anemia may potentiate the risk of cardiac arrhythmias such as ventricular tachycardia and torsade de pointes because of the hypokalemia that may develop during the early phase of vitamin B12 therapy due to increasing potassium requirements as normal erythropoiesis is established. Hypokalemia is a known risk factor for ventricular arrhythmias associated with QT interval prolongation.', NULL, 'Caution is advised when arsenic trioxide is administered to patients requiring vitamin B12 for severe megaloblastic anemia.', NULL, 'Caution is advised when arsenic trioxide is administered to patients requiring vitamin B12 for severe megaloblastic anemia. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. During therapy, potassium concentrations should be kept above 4 mEq/dL.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31624/', '[1] "Product Information. Trisenox (arsenic trioxide)" Cephalon Inc, West Chester, PA.[2] "Product Information. Nascobol (cyanocobalamin nasal)." Schwarz Pharma, Mequon, WI.[3] "Product Information. Hydro-Cobex (hydroxocobalamin)." Major Pharmaceuticals Inc, Rosemont, IL.', 1767365203);
