INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31982, 'Ritonavir', 'Artemether', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of artemether and lumefantrine, both of which are primarily metabolized by the isoenzyme. Coadministration with artemether may decrease the plasma concentrations of CYP450 3A4 substrates such as protease inhibitors, macrolide antibiotics, and azole antifungal agents.', NULL, 'No dose adjustment of artemether-lumefantrine is necessary when administered in combination with potent CYP450 3A4 inhibitors such as azole antifungal agents, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'No dose adjustment of artemether-lumefantrine is necessary when administered in combination with potent CYP450 3A4 inhibitors such as azole antifungal agents, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. However, caution is advised due to increased risk of QT interval prolongation. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Patients should also be monitored for reduced effects or potential loss of efficacy of the concomitant drug(s), especially antiretroviral agents.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/32035/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Coartem (artemether-lumefantrine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Coartem (artemether-lumefantrine)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
