    ) VALUES (1343179, 'Bromocriptine', 'Sumatriptan', 'Major', 'Coadministration of two or more 5-HT1 receptor agonists and/or ergotamine-containing or ergot-type medications may potentiate the risk of vasospastic reactions, including coronary artery vasospasm, peripheral vascular ischemia and colonic ischemia, due to additive pharmacologic effects.', NULL, 'The concomitant use of two or more 5-HT1 receptor agonists and/or ergotamine-containing or ergot-type medications, or use within 24 hours of each other, is considered contraindicated.', NULL, 'The concomitant use of two or more 5-HT1 receptor agonists and/or ergotamine-containing or ergot-type medications, or use within 24 hours of each other, is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
