INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (32284, 'Morphine', 'Metoclopramide', 'Moderate', 'By diminishing gastrointestinal motility, narcotic analgesics may antagonize the pharmacologic effects of gastrointestinal prokinetic agents. In addition, concomitant use may increase central nervous system effects such as sedation, dizziness, confusion, and mental depression. Gastrointestinal prokinetic agents may alter the absorption characteristics of some controlled release narcotic analgesic preparations.', NULL, 'The potential for reduced efficacy of gastrokinetic agents should be considered during coadministration with opioid analgesics.', NULL, 'The potential for reduced efficacy of gastrokinetic agents should be considered during coadministration with opioid analgesics. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Antagonism, Absorption', 'Eluxadoline, Diphenoxylate, Difenoxin, Naltrexone', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/32337/', '[1] Manara AR, Shelly MP, Quinn K, Park GR "The effect of metoclopramide on the absorption of oral controlled release morphine." Br J Clin Pharmacol 25 (1988): 518-21[2] Greiff JMC, Rowbotham D "Pharmacokinetic drug interactions with gastrointestinal motility modifying agents." Clin Pharmacokinet 27 (1994): 447-61[3] "Product Information. Reglan (metoclopramide)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Warrington SJ, Ankier SI, Turner P "Evaluation of