INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343318, 'Alteplase', 'Dicoumarol', 'Major', 'Although warfarin is commonly administered for the prevention of embolism in certain patients following thrombolytic therapy (e.g., myocardial infarction), oral anticoagulants may increase the risk of serious bleeding when administered before, during, or after fibrinolytic agents.', 'DDInter', 1767317475, 'Alteplase is contraindicated in patients with acute ischemic stroke who are currently taking oral anticoagulants and have an INR greater than 1.7. For other indications, risk versus benefit should be carefully weighed if patients who are currently taking oral anticoagulants (INR 2 to 3) are to receive thrombolytic therapy and patient selection should adhere to currently recommended criteria. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. If oral anticoagulants are initiated following thrombolytic therapy, the dosage should be adjusted as appropriate to maintain therapeutic INR values. Patients should be closely monitored for clinical and laboratory signs of bleeding.', 'Synergism', 'Alteplase is contraindicated in patients with acute ischemic stroke who are currently taking oral anticoagulants and have an INR greater than 1.', NULL, NULL, 'Major', 'pharmacodynamic');
