INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343321, 'Drotrecogin alfa', 'Alteplase', 'Major', 'Concurrent administration of thrombolytic agents and drotrecogin alfa may increase the risk of serious or life-threatening bleeding complications due to additive or synergistic effects on the clotting cascade. Drotrecogin alfa inactivates blood clotting Factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT). Most severely ill septic patients are already at a high risk of bleeding because of coagulopathies associated with prolonged APTT and prothrombin times (PT).', 'DDInter', 1767317475, 'When making the decision to administer drotrecogin alfa, the increased risk of bleeding versus the benefits of therapy should be carefully considered in seriously ill sepsis patients who have recently (within 3 days) received thrombolytic agents. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.', 'Synergism', 'When making the decision to administer drotrecogin alfa, the increased risk of bleeding versus the benefits of therapy should be carefully considered in seriously ill sepsis patients who have recently (within 3 days) received thrombolytic agents.', NULL, NULL, 'Major', 'pharmacodynamic');
