INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343391, 'Alteplase', 'Ticagrelor', 'Moderate', 'The concomitant use of ticagrelor with other platelet inhibitors, anticoagulants, thrombolytic agents, or nonsteroidal anti-inflammatory drugs may increase the risk of bleeding.', 'DDInter', 1767317475, 'Caution is recommended when these drugs are used concomitantly with ticagrelor or within 24 hours of ticagrelor dosing. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended when these drugs are used concomitantly with ticagrelor or within 24 hours of ticagrelor dosing.', NULL, NULL, 'Moderate', 'pharmacodynamic');
