INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343416, 'Verteporfin', 'Tretinoin (topical)', 'Moderate', 'Concomitant use of verteporfin with other known photosensitizing agents may increase the risk of photosensitivity reactions. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 1767317475, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of skin or eyes to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) for 5 days after administration of verteporfin.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343417, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Altretamine', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 1767317475, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343418, 'Amitriptyline', 'Altretamine', 'Moderate', 'The coadministration of altretamine with a monoamine oxidase inhibitor (MAOI) or tricyclic antidepressant may result in severe orthostatic hypotension.', 'DDInter', 1767317475, 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients. All patients receiving the combination should be advised to avoid rising abruptly from a sitting or lying position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343419, 'Amobarbital', 'Altretamine', 'Moderate', 'According to a rat model, the coadministration with phenobarbital (PB) may decrease the plasma, liver, and tumor concentrations of altretamine, as well as reduce its antitumor activity. The likely mechanism is PB induction of altretamine metabolism via CYP450 isoenzymes, resulting in decreased time of exposure of tumor cells to altretamine and its active metabolites.', 'DDInter', 1767317475, 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible. The same precaution may be applicable to other barbiturates because of their potential to induce CYP450 isoenzymes.', 'Metabolism', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343420, 'Amoxapine', 'Altretamine', 'Moderate', 'The coadministration of altretamine with a monoamine oxidase inhibitor (MAOI) or tricyclic antidepressant may result in severe orthostatic hypotension.', 'DDInter', 1767317475, 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients. All patients receiving the combination should be advised to avoid rising abruptly from a sitting or lying position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343421, 'Anakinra', 'Altretamine', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343422, 'Altretamine', 'Anthrax vaccine', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343423, 'Altretamine', 'Azathioprine', 'Moderate', 'The use of azathioprine with other immunosuppressive or myelosuppressive agents may result in additive hematologic toxicities and increased risk of infections, particularly in transplant patients. Azathioprine alone may cause dose-related and potentially life-threatening bone marrow suppression, although it is usually reversible when managed promptly. Leucopenia, anemia, thrombocytopenia, and rarely, agranulocytosis, pancytopenia, and aplastic anemia have been reported. Dose-related reductions in numbers of circulating total white cells, granulocytes, and lymphocytes may also occur. Treatment with azathioprine alone or in combination with other immunosuppressants, particularly corticosteroids, has been associated with increased susceptibility to infections including severe or atypical infection and reactivation with varicella zoster virus, hepatitis B, cytomegalovirus, and other infectious agents.', 'DDInter', 1767317475, 'Concomitant use of azathioprine with other immunosuppressive or myelosuppressive agents should be avoided whenever possible. Close clinical and laboratory monitoring for hematologic toxicity is advised if coadministration is required. Since azathioprine is considered a slow-acting drug, delayed myelosuppression may occur, and effects may persist even after the drug has been discontinued. Prompt reduction in dosage or temporary withdrawal of azathioprine may be necessary if a persistently low or rapid decline in leucocyte count occurs, or if there is other evidence of bone marrow depression.', 'Synergism', 'Concomitant use of azathioprine with other immunosuppressive or myelosuppressive agents should be avoided whenever possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343424, 'Altretamine', 'Baricitinib', 'Major', 'Coadministration of baricitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. The most common serious infections with baricitinib treatment include pneumonia, herpes zoster, and urinary tract infection. Opportunistic infections include tuberculosis, multidermatomal herpes zoster, esophageal candidiasis, pneumocystosis, acute histoplasmosis, cryptococcosis, cytomegalovirus, and BK virus. Lymphoma and other malignancies have also been observed with baricitinib use, with or without concomitant immunosuppressants.', 'DDInter', 1767317475, 'Close monitoring for the development of infection is recommended if baricitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and baricitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If a serious infection, an opportunistic infection, or sepsis develops, baricitinib should be interrupted until the infection is controlled.', 'Synergism', 'Close monitoring for the development of infection is recommended if baricitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343425, 'Altretamine', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317475, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343426, 'Altretamine', 'Bifidobacterium longum infantis', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343427, 'Altretamine', 'Butabarbital', 'Moderate', 'According to a rat model, the coadministration with phenobarbital (PB) may decrease the plasma, liver, and tumor concentrations of altretamine, as well as reduce its antitumor activity. The likely mechanism is PB induction of altretamine metabolism via CYP450 isoenzymes, resulting in decreased time of exposure of tumor cells to altretamine and its active metabolites.', 'DDInter', 1767317475, 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible. The same precaution may be applicable to other barbiturates because of their potential to induce CYP450 isoenzymes.', 'Metabolism', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343428, 'Altretamine', 'Butalbital', 'Moderate', 'According to a rat model, the coadministration with phenobarbital (PB) may decrease the plasma, liver, and tumor concentrations of altretamine, as well as reduce its antitumor activity. The likely mechanism is PB induction of altretamine metabolism via CYP450 isoenzymes, resulting in decreased time of exposure of tumor cells to altretamine and its active metabolites.', 'DDInter', 1767317475, 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible. The same precaution may be applicable to other barbiturates because of their potential to induce CYP450 isoenzymes.', 'Metabolism', 'Until further data are available, the concomitant use of altretamine and phenobarbital should probably be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343429, 'Altretamine', 'Canakinumab', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343430, 'Altretamine', 'Candida albicans', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343431, 'Altretamine', 'Certolizumab pegol', 'Major', 'The use of tumor necrosis factor (TNF) blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Serious infections and sepsis, including fatalities, have been reported with the use of TNF blockers, particularly in patients on concomitant immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. Concomitant use of TNF blockers with other immunosuppressants such as azathioprine or mercaptopurine may also increase the risk of a rare and often fatal cancer of white blood cells known as hepatosplenic T-Cell lymphoma (HSTCL).', 'DDInter', 1767317475, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections. TNF blocker therapy should be discontinued if a serious infection or sepsis occurs. Close monitoring for signs and symptoms of HSTCL (e.g., splenomegaly, hepatomegaly, abdominal pain, persistent fever, night sweats, weight loss) is also recommended during use of TNF blockers, particularly in combination with other immunosuppressants such as azathioprine and mercaptopurine.', 'Synergism', 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343432, 'Altretamine', 'Chloramphenicol', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity. Serious and fatal blood dyscrasias (aplastic anemia, hypoplastic anemia, thrombocytopenia and granulocytopenia) have been reported after the administration of chloramphenicol.', 'DDInter', 1767317475, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343433, 'Altretamine', 'Chloramphenicol (ophthalmic)', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity. Serious and fatal blood dyscrasias (aplastic anemia, hypoplastic anemia, thrombocytopenia and granulocytopenia) have been reported after the administration of chloramphenicol.', 'DDInter', 1767317475, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343434, 'Altretamine', 'Vibrio cholerae CVD 103-HgR strain live antigen', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343435, 'Altretamine', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Moderate', 'There are no data regarding the use of live, attenuated cholera vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 1767317475, 'The safety and efficacy of live, attenuated cholera vaccine have not been established in patients receiving immunosuppressive therapies.', 'Others', 'The safety and efficacy of live, attenuated cholera vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343436, 'Altretamine', 'Cimetidine', 'Moderate', 'According to a rat model, the coadministration with cimetidine may increase the half-life and toxicity of altretamine. The likely mechanism is cimetidine inhibition of altretamine metabolism via CYP450 isoenzymes.', 'DDInter', 1767317475, 'Based on animal data as well as human case reports of increased toxicity when other antineoplastic agents have been administered with cimetidine, the concomitant use of altretamine and cimetidine should generally be avoided if possible. Ranitidine may be a safer alternative if H2-antagonist therapy is necessary. Famotidine and nizatidine may also be considered, since they have not been shown to interfere with CYP450 metabolism.', 'Metabolism', 'Based on animal data as well as human case reports of increased toxicity when other antineoplastic agents have been administered with cimetidine, the concomitant use of altretamine and cimetidine should generally be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343437, 'Altretamine', 'Cinoxacin', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343438, 'Altretamine', 'Ciprofloxacin', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343439, 'Altretamine', 'Cladribine', 'Major', 'The use of cladribine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Cladribine alone may cause severe and prolonged myelosuppression, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible. Acute short-term therapy with corticosteroids can be administered. Caution is advised if IV cladribine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. In patients who have previously been treated with immunomodulatory or immunosuppressive drugs, consider potential additive effect, mode of action, and duration of effect of the other drugs prior to initiation of cladribine. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343440, 'Altretamine', 'Clomipramine', 'Moderate', 'The coadministration of altretamine with a monoamine oxidase inhibitor (MAOI) or tricyclic antidepressant may result in severe orthostatic hypotension.', 'DDInter', 1767317475, 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients. All patients receiving the combination should be advised to avoid rising abruptly from a sitting or lying position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343441, 'Altretamine', 'Clozapine', 'Major', 'Coadministration of clozapine with other agents that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', 'DDInter', 1767317475, 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function. Agents that may be significantly myelotoxic include antineoplastic drugs, some anticonvulsant and antirheumatic medications, albendazole, chloramphenicol, colchicine, dapsone, interferons, linezolid, pentamidine, procainamide, and zidovudine.', 'Synergism', 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343442, 'Altretamine', 'Coccidioides immitis spherule', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343443, 'Altretamine', 'Deferiprone', 'Major', 'Coadministration of deferiprone and other drugs that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity. Deferiprone alone can cause agranulocytosis, which may be preceded by neutropenia.', 'DDInter', 1767317475, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided. Otherwise, close monitoring of absolute neutrophil count (ANC) is recommended if coadministration is required.', 'Synergism', 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
