INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33102, 'Brentuximab vedotin', 'Asparaginase Escherichia coli', 'Moderate', 'Coadministration of brentuximab vedotin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The risk of hepatic injury should be considered when brentuximab vedotin is used with other agents that are potentially hepatotoxic (e.', NULL, 'The risk of hepatic injury should be considered when brentuximab vedotin is used with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations. Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33155/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc, Bothell, WA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33103, 'Bromfenac', 'Asparaginase Escherichia coli', 'Moderate', 'Asparaginase therapy may lead to imbalances in coagulation factors predisposing the patient to bleeding or thrombosis. Theoretically, concurrent use of anticoagulants and asparaginase may increase the risk of bleeding. Hemorrhages grade 3 and above and CNS hemorrhages have been observed with asparaginase treatment.', NULL, 'Coagulation parameters should be monitored at baseline and periodically during concomitant treatment with asparaginase and anticoagulants such as heparin, dipyridamole, aspirin and nonsteroidal anti-inflammatory drugs.', NULL, 'Coagulation parameters should be monitored at baseline and periodically during concomitant treatment with asparaginase and anticoagulants such as heparin, dipyridamole, aspirin and nonsteroidal anti-inflammatory drugs. Patients should be advised to promptly report any signs and symptoms of bleeding.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33156/', '[1] Caruso V, Iacoviello L, Di Castelnuovo A, et.al "Thrombotic complications in childhood acute lymphoblastic leukemia: a meta-analysis of 17 prospective studies comprising 1752 pediatric patients." Blood 108 (2006): 2216-22[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Duarte X, Esteves S, Neto AM, Pereira F "Incidence and risk factors for central nervous system thrombosis in paediatric acute lymphoblastic leukaemia during intensive asparaginase treatment: a single-centre study." Br J Haematol 174 (2016): 280-91[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Kieslich M, Porto L, Lanfermann H, Jacobi G, Schwabe D, Bohles H "Cerebrovascular complications of L-asparaginase in the therapy of acute lymphoblastic leukemia." J Pediatr Hematol Oncol 25 (2003): 484-7[6] Grace RF, DeAngelo DJ, Stevenson KE, et.al "The use of prophylactic anticoagulation during induction and consolidation chemotherapy in adults with acute lymphoblastic leukemia." J Thromb Thrombolysis 45 (2018): 306-14[7] Mitchell L, Andrew M, Hanna K, et.al "Trend to efficacy and safety using antithrombin concentrate in prevention of thrombosis in children receiving l-asparaginase for acute lymphoblastic leukemia. Results of the PAARKA study." Thromb Haemost 90 (2003): 235-44[8] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[9] "Product Information. Erwinaze (asparaginase Erwinia chrysanthemi)." EUSA Pharma, Newtown, PA.[10] "Product Information. Asparlas (calaspargase pegol)." Servier, Boston, MA.[11] Truelove E, Fielding AK, Hunt BJ "The coagulopathy and thrombotic risk associated with L-asparaginase treatment in adults with acute lymphoblastic leukaemia." Leukemia 27 (2013): 553-9[12] Elice F, Rodeghiero F "Hematologic malignancies and thrombosis." Thromb Res 129 (2012): 360-6[13] "Product Information. Elspar (asparaginase)." Merck & Co, Inc, West Point, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33104, 'Budesonide', 'Asparaginase Escherichia coli', 'Moderate', 'INTERVAL: The administration of asparaginase with or immediately preceding prednisone may increase the risk of hyperglycaemia. Concomitant use of asparaginase and corticosteroids may increase the risk of thrombosis or hemorrhage. The proposed mechanism may be related to asparaginase-induced fluctuation of coagulation proteins.', NULL, 'Close monitoring for clinical and laboratory evidence of hyperglycemia and/or altered coagulation is also recommended at baseline and periodically throughout concurrent treatment with corticosteroids and asparaginase-derived products.', NULL, 'Close monitoring for clinical and laboratory evidence of hyperglycemia and/or altered coagulation is also recommended at baseline and periodically throughout concurrent treatment with corticosteroids and asparaginase-derived products. Patients should be advised to promptly report any signs and symptoms of hyperglycemia, bleeding, blood clots or bone pain.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33157/', '[1] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[2] "Product Information. Erwinaze (asparaginase Erwinia chrysanthemi)." EUSA Pharma, Newtown, PA.[3] Athale UH, Atkinson HH, Athale A, Nayiager T, Chan A "Effect of asparaginase and dexamethasone on FVIIa-at complex and F1.2 in children with acute lymphoblastic leukemia: evidence of a hypercoagulable state." Blood 126 (2015): 3522[4] Christ TN, Stock W, Knoebel RW "Incidence of asparaginase-related hepatotoxicity, pancreatitis, and thrombotic events in