INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33130, 'Clofarabine', 'Asparaginase Escherichia coli', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33183/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33131, 'Clopidogrel', 'Asparaginase Escherichia coli', 'Moderate', 'Asparaginase therapy may lead to imbalances in coagulation factors predisposing the patient to bleeding or thrombosis. Theoretically, concurrent use of anticoagulants and asparaginase may increase the risk of bleeding. Hemorrhages grade 3 and above and CNS hemorrhages have been observed with asparaginase treatment.', NULL, 'Coagulation parameters should be monitored at baseline and periodically during concomitant treatment with asparaginase and anticoagulants such as heparin, dipyridamole, aspirin and nonsteroidal anti-inflammatory drugs.', NULL, 'Coagulation parameters should be monitored at baseline and periodically during concomitant treatment with asparaginase and anticoagulants such as heparin, dipyridamole, aspirin and nonsteroidal anti-inflammatory drugs. Patients should be advised to promptly report any signs and symptoms of bleeding.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33184/', '[1] Caruso V, Iacoviello L, Di Castelnuovo A, et.al "Thrombotic complications in childhood acute lymphoblastic leukemia: a meta-analysis of 17 prospective studies comprising 1752 pediatric patients." Blood 108 (2006): 2216-22[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Duarte X, Esteves S, Neto AM, Pereira F "Incidence and risk factors for central nervous system thrombosis in paediatric acute lymphoblastic leukaemia during intensive asparaginase treatment: a single-centre study." Br J Haematol 174 (2016): 280-91[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Kieslich M, Porto L, Lanfermann H, Jacobi G, Schwabe D, Bohles H "Cerebrovascular complications of L-asparaginase in the therapy of acute lymphoblastic leukemia." J Pediatr Hematol Oncol 25 (2003): 484-7[6] Grace RF, DeAngelo DJ, Stevenson KE, et.al "The use of prophylactic anticoagulation during induction and consolidation chemotherapy in adults with acute lymphoblastic leukemia." J Thromb Thrombolysis 45 (2018): 306-14[7] Mitchell L, Andrew M, Hanna K, et.al "Trend to efficacy and safety using antithrombin concentrate in prevention of thrombosis in children receiving l-asparaginase for acute lymphoblastic leukemia. Results of the PAARKA study." Thromb Haemost 90 (2003): 235-44[8] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[9] "Product Information. Erwinaze (asparaginase Erwinia chrysanthemi)." EUSA Pharma, Newtown, PA.[10] "Product Information. Asparlas (calaspargase pegol)." Servier, Boston, MA.[11] Truelove E, Fielding AK, Hunt BJ "The coagulopathy and thrombotic risk associated with L-asparaginase treatment in adults with acute lymphoblastic leukaemia." Leukemia 27 (2013): 553-9[12] Elice F, Rodeghiero F "Hematologic malignancies and thrombosis." Thromb Res 129 (2012): 360-6[13] "Product Information. Elspar (asparaginase)." Merck & Co, Inc, West Point, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33132, 'Clotrimazole', 'Asparaginase Escherichia coli', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33185/', '[1] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bodmer M, Sulz M, Stadlmann S, Droll A, Terracciano L, Krahenbuhl S "Fatal liver failur