INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343605, 'Isavuconazonium', 'Norethisterone', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', 'DDInter', 1767317475, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343606, 'Ivacaftor', 'Ethinylestradiol', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 1767317475, 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343607, 'Norethisterone', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 1767317475, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343608, 'Norethisterone', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343609, 'Norethisterone', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. In vitro and in vivo studies have shown that both estrogens and progestins are partially metabolized by CYP450 3A4. Studies of letermovir with midazolam, a CYP450 3A4 probe substrate, have shown the net effect of letermovir on the CYP450 3A4 isoenzyme is moderate inhibition.', 'DDInter', 1767317475, 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy. When choosing an oral contraceptive, consider choosing an ethinyl estradiol-levonorgestrel product as this combination has been studied with letermovir and no clinically significant interaction has been found.', 'Metabolism', 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343610, 'Ethinylestradiol', 'Linaclotide', 'Moderate', 'RECOMMENDED: The absorption and efficacy of oral contraceptives may be reduced if severe diarrhea occurs with linaclotide treatment. Linaclotide has been associated with severe and prolonged diarrhea.', 'DDInter', 1767317475, 'In patients who develop severe diarrhea during treatment with linaclotide, European labeling recommends use of an additional contraceptive method to prevent possible failure of oral contraception.', 'Absorption', 'In patients who develop severe diarrhea during treatment with linaclotide, European labeling recommends use of an additional contraceptive method to prevent possible failure of oral contraception.', NULL, NULL, 'Moderate', 'pharmacodynamic');
