INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343862, 'Diclofenac (topical)', 'Rofecoxib', 'Moderate', 'Nonsteroidal anti-inflammatory drugs may be absorbed systemically following topical administration. Concomitant use with systemic nonsteroidal anti-inflammatory drugs may result in increased adverse effects such as gastrointestinal toxicity (inflammation, bleeding, ulceration, perforation), cardiovascular thrombotic events, hepatotoxicity (liver enzyme elevations), renal toxicity, fluid retention, edema, hypertension, and inhibition of platelet aggregation.', 'DDInter', 1767317475, 'Patients treated with topical preparations of nonsteroidal anti-inflammatory drugs should avoid or limit the use of systemic nonsteroidal anti-inflammatory drugs. Although systemic exposure is generally low following topical administration, absorption may be increased with frequent applications; use of large quantities or over large areas of skin; use on compromised or diseased skin or open wounds; and use of occlusive dressings or heating pads over application areas.', 'Synergism', 'Patients treated with topical preparations of nonsteroidal anti-inflammatory drugs should avoid or limit the use of systemic nonsteroidal anti-inflammatory drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
