INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1343921, 'Amphotericin B', 'Amikacin', 'Moderate', 'The potential nephrotoxicity associated with amphotericin B and aminoglycosides may be additive.', 'DDInter', 1767317475, 'Just before the start and just before the end of each amphotericin infusion, hydration of the patient with 500 mL of normal saline may be helpful. The patient''s renal function should be monitored frequently during therapy.', 'Synergism', 'Just before the start and just before the end of each amphotericin infusion, hydration of the patient with 500 mL of normal saline may be helpful.', NULL, NULL, 'Moderate', 'pharmacodynamic');
