INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344000, 'Amphotericin B', 'Methoxyflurane', 'Moderate', 'Coadministration of methoxyflurane with other nephrotoxic agents may increase the risk of renal impairment. The precise mechanism has not been delineated.', 'DDInter', 1767317475, 'Concomitant use of methoxyflurane with other potentially nephrotoxic agents should generally be avoided. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Concomitant use of methoxyflurane with other potentially nephrotoxic agents should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344001, 'Amphotericin B', 'Methylprednisolone', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317475, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344002, 'Amphotericin B', 'Metocurine', 'Moderate', 'Amphotericin commonly causes renal potassium and magnesium wasting. As a result, serum potassium is decreased, and the pharmacologic effects of some neuromuscular blocking agents are enhanced.', 'DDInter', 1767317475, 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', 'Excretion', 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344003, 'Amphotericin B', 'Metolazone', 'Moderate', 'The concomitant use of amphotericin and potassium-wasting diuretics may result in increased risk of hypokalemia due to additive effects.', 'DDInter', 1767317475, 'Patients receiving diuretics with amphotericin should be monitored closely for development of hypokalemia. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving diuretics with amphotericin should be monitored closely for development of hypokalemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344004, 'Amphotericin B', 'Micafungin', 'Moderate', 'The concomitant use of micafungin and amphotericin B may increase systemic exposure to amphotericin. The mechanism is unknown. Coadministration resulted in a 30% increase in amphotericin exposure. Micafungin pharmacokinetics were not affected.', 'DDInter', 1767317475, 'The European manufacturers recommend that these drugs should only be coadministered if the potential benefits outweigh the risks. Patients should be monitored for amphotericin toxicity.', 'Others', 'The European manufacturers recommend that these drugs should only be coadministered if the potential benefits outweigh the risks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344005, 'Amphotericin B', 'Miconazole', 'Minor', 'Imidazole and triazole antifungal agents may antagonize the effects of amphotericin. The clinical significance is unknown. Caution is urged if combination therapy is necessary.', 'DDInter', 1767317475, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
