INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33762, 'Clopidogrel', 'Acetylsalicylic acid', 'Moderate', 'Clopidogrel has been shown to potentiate the inhibition of platelet aggregation due to aspirin. The safety of chronic administration of aspirin or other salicylates with clopidogrel has not been established.', NULL, 'Until further data are available, caution is recommended, especially in patients at risk of bleeding (i.', NULL, 'Until further data are available, caution is recommended, especially in patients at risk of bleeding (i.e., GI ulceration), if clopidogrel is coadministered on a long-term basis with drugs that may cause GI lesions. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, red or black stools, or bloody or coffee-ground emesis. Patients should also be counseled to avoid any other over-the-counter salicylate products.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33815/', '[1] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000): 305-13[2] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb, Princeton, NJ.', 1767365203);
