INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33779, 'Dexlansoprazole', 'Acetylsalicylic acid', 'Minor', 'Coadministration with proton pump inhibitors may decrease the oral bioavailability of aspirin and other salicylates. Proton pump inhibitors may enhance the release rate of salicylates from enteric-coated formulations due to premature disruption of the coating and intragastric release of the drug secondary to an increase in gastric pH. The clinical significance of this interaction is unknown. Theoretically, it may increase the risk of gastric adverse effects associated with salicylates.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33832/', '[1] Anand BS, Sanduja SK, Lichetenberger LM "Effect of omeprazole on the bioavailability of aspirin: a randomized controlled study on healthy volunteers." Gastroenterology 116 (1999): A371[2] Inarrea P, Esteva F, Cornudella R, Lanas A "Omeprazole does not interfere with the antiplatelet effect of low-dose aspirin in man." Scand J Gastroenterol 35 (2000): 242-6[3] Nefesoglu FZ, Ayanoglu-Dulger G, Ulusoy NB, Imeryuz N "Interaction of omeprazole with enteric-coated salicylate tablets." Int J Clin Pharmacol Ther 36 (1998): 549-53[4] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33780, 'Diclofenamide', 'Acetylsalicylic acid', 'Major', 'The combination of large doses of salicylates and oral carbonic anhydrase inhibitors (CAI) may rarely result in severe metabolic acidosis and/or salicylate toxicity. The mechanism is unknown but may involve salicylate-induced displacement of CAIs from plasma protein-binding sites and reduced renal clearance, or CAI-induced plasma pH changes resulting in increased amounts of unionised salicylates entering the CNS.', NULL, 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended.', NULL, 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended. If coadministration is necessary, careful monitoring of the patient''s mental status and acid base balance is strongly recommended. Patients should be advised to promptly notify their physicians if they experience symptoms such as lethargy, tinnitus, confusion, nausea, vomiting, or hyperventilation.', 'Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33833/', '[1] Sweeney KR, Chapron DJ, Brandt JL, Gomolin IH, Feig PU, Kramer PA "Toxic interaction between acetazolamide and salicylate: case reports and a pharmacokinetic explanation." Clin Pharmacol Ther 40 (1986): 518-24[2] Sweeney KR, Chapron DJ, Kramer PA "Effect of salicylate on serum protein binding and red blood cell uptake of acetazolamide in vitro." J Pharm Sci 77 (1988): 751-6[3] Cowan RA, Hartnell GG, Lowdell CP, Baird IM, Leak AM "Metabolic acidosis induced by carbonic anhydrase inhibitors and salicylates in patients with normal renal function." Br Med J (Clin Res Ed) 289 (1984): 347-8[4] Rousseau P, Fuentevilla-Clifton A "Acetazolamide and salicylate interaction in the elderly: a case report." J Am Geriatr Soc 41 (1993): 868-9[5] "Product Information. Diamox (acetazolamide)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Azopt (brinzolamide ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] Cowan RA, Hartnell GG, Lowdell CP, Baird IM, Leak AM "Metabolic acidosis induced by carbonic anhydrase inhibitors and salicylates in patients with normal renal function." Br Med J (Clin Res Ed) 289 (1984): 347-8[10] Sweeney KR, Chapron DJ, Antal EJ, Kramer PA "Differential effects of flurbiprofen and aspirin on acetazolamide disposition in humans." Br J Clin Pharmacol 27 (1989): 866-9[11] "Product Information. Trusopt (dorzolamide ophthalmic)." Merck & Co, Inc, West Point, PA.[12] Anderson CJ, Kaufman PL, Sturm RJ "Toxicity of combined therapy with carbonic anhydrase inhibitors and aspirin." Am J Ophthalmol 86 (1978): 516-9[13] Sweeney KR, Chapron DJ, Brandt JL, Gomolin IH, Feig PU, Kramer PA "Toxic interaction between acetazolamide and salicylate: case reports and a pharmacokinetic explanation." Clin Pharmacol Ther 40 (1986): 518-24[14] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[15] Favre L, Vallotton MB "Relationship of renal prostaglandins to three diuretics." Prostaglandins Leukot Med 14 (1984): 313-9[16] Cowan RA, Hartnell GG, Lowdell CP, Baird IM, Leak AM "Metabolic acidosis induced by carbonic anhydrase inhibitors and salicylates in patients with normal renal function." Br Med J (Clin Res Ed) 289 (1984): 347-8', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33781, 'Dipyridamole', 'Diclofenac', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33834/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365203);
