INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33788, 'Acetylsalicylic acid', 'Valproic acid', 'Moderate', 'Salicylates, particularly aspirin, may displace valproate from protein binding sites and inhibit its clearance. Increased therapeutic and toxic effects may be expected to occur. This interaction is more likely with large or prolonged doses of salicylates.', NULL, 'Small single doses of salicylates are unlikely to cause significant effects.', NULL, 'Small single doses of salicylates are unlikely to cause significant effects. However, patients who take large doses of salicylates or over a prolonged period of time should be closely monitored for clinical and laboratory evidence of valproate toxicity and hepatotoxicity. Free fraction of valproate may be particularly helpful in detecting this interaction. Patients should be advised to notify their physician if they experience possible symptoms of toxicity (e.g., malaise, weakness, lethargy, drowsiness, nausea, vomiting, or abdominal pain).', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33841/', '[1] Farrell K, Orr JM, Abbott FS, et al "The effect of acetylsalicylic acid on serum free valproate concentrations and valproate clearance in children." J Pediatr 101 (1982): 142-4[2] Orr JM, Abbott FS, Farrell K, Ferguson S, Sheppard I, Godolphin W "Interaction between valproic acid and aspirin in epileptic children: serum protein binding and metabolic effects." Clin Pharmacol Ther 31 (1982): 642-9[3] Dasgupta A, Jacques M "Reduced in vitro displacement of valproic acid from protein binding by salicylate in uremic sera compared with normal sera - role of uremic compounds." Am J Clin P