INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33807, 'Esomeprazole', 'Acetylsalicylic acid', 'Minor', 'Coadministration with proton pump inhibitors may decrease the oral bioavailability of aspirin and other salicylates. Proton pump inhibitors may enhance the release rate of salicylates from enteric-coated formulations due to premature disruption of the coating and intragastric release of the drug secondary to an increase in gastric pH. The clinical significance of this interaction is unknown. Theoretically, it may increase the risk of gastric adverse effects associated with salicylates.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33860/', '[1] Anand BS, Sanduja SK, Lichetenberger LM "Effect of omeprazole on the bioavailability of aspirin: a randomized controlled study on healthy volunteers." Gastroenterology 116 (1999): A371[2] Inarrea P, Esteva F, Cornudella R, Lanas A "Omeprazole does not interfere with the antiplatelet effect of low-dose aspirin in man." Scand J Gastroenterol 35 (2000): 242-6[3] Nefesoglu FZ, Ayanoglu-Dulger G, Ulusoy NB, Imeryuz N "Interaction of omeprazole with enteric-coated salicylate tablets." Int J Clin Pharmacol Ther 36 (1998): 549-53[4] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365203);
