INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33881, 'Acetylsalicylic acid', 'Methazolamide', 'Major', 'The combination of large doses of salicylates and oral carbonic anhydrase inhibitors (CAI) may rarely result in severe metabolic acidosis and/or salicylate toxicity. The mechanism is unknown but may involve salicylate-induced displacement of CAIs from plasma protein-binding sites and reduced renal clearance, or CAI-induced plasma pH changes resulting in increased amounts of unionised salicylates entering the CNS.', NULL, 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended.', NULL, 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended. If coadministration is necessary, careful monitoring of the patient''s mental status and acid base balance is strongly recommended. Patients should be advised to promptly notify their physicians if they experience symptoms such as lethargy, tinnitus, confusion, nausea, vomiting, or hyperventilation.', 'Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33934/', '[1] Sweeney KR, Chapron DJ, Brandt JL, Gomolin IH, Feig PU, Kramer PA "Toxic interaction between acetazolamide and salicylate: case reports and a pharmacokinetic explanation." Clin Pharmacol Ther 40 (1986): 518-24[2] Sweeney KR, Chapron DJ, Kramer PA "Effect of salicylate on serum protein binding and red blood cell uptake of acetazolamide in vitro." J Pharm Sci 77 (1988): 751-6[3] Cowan RA, Hartnell GG, Lowdell CP, Baird IM, Leak AM "Metabolic acidosis induced by carbonic anhydrase inhibitors and salicylates in patients with normal renal function." Br Me