INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33963, 'Acetylsalicylic acid', 'Streptomycin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34016/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33964, 'Acetylsalicylic acid', 'Sulfinpyrazone', 'Moderate', 'Salicylates may antagonize the uricosuric effect of sulfinpyrazone. The exact mechanism of interaction is unknown, although therapeutic doses of aspirin have been shown to increase plasma clearance of sulfinpyrazone.', NULL, 'Salicylates should not be used during sulfinpyrazone therapy for gouty arthritis.', NULL, 'Salicylates should not be used during sulfinpyrazone therapy for gouty arthritis. However, occasional small doses of salicylates are not expected to interact significantly.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34017/', '[1] Ali M, McDonald JW "Interference by sulfinpyrazone and salicylate of aspirin inhibition of platelet cyclooxygenase activity." Prostaglandins Med 3 (1979): 327-32[2] "Product Information. Anturane (sulfinpyrazone)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Buchanan MR, Endrenyi L, Giles AR, Rosenfeld J "The effect of aspirin on the pharmacokinetics of sulfinpyrazone in man." Thromb Res Suppl 4: (1983): 145-52[4] Ranhosky A, Kempthorne-Rawson J, the Intravenous Dipyridamole Thallium Imaging Study Group "The safety of intravenous dipyridamole thallium myocardial perfusion imaging." Circulation 81 (1990): 1205-9[5] "Product Information. Persantine (dipyridamole)." Boehringer-Ingelheim, Ridgefield, CT.[6] Smits P, Aengevaeren WR, Corstens FH, Thien T "Caffeine reduces dipyridamole-induced myocardial ischemia." J Nucl Med 30 (1989): 1723-6', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (33965, 'Acetylsalicylic acid', 'Sulindac', 'Moderate', 'The combined use of low-dose or high-dose aspirin with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation. Aspirin at anti-inflammatory dosages or higher may also decrease the plasma concentrations of many NSAIDs. The decreases have ranged from none or small (piroxicam, meloxicam, naproxen, tolmetin) to substantial (flurbiprofen, ibuprofen). However, the therapeutic response does not appear to be affected.', NULL, 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs.', NULL, 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs. Concomitant administration of NSAIDs is considered contraindicated or not recommended with aspirin at analgesic/anti-inflammatory dosages by many NSAID manufacturers. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as abdominal pain, bloating, sudden dizziness or lightheadedness, nausea, vomiting, hematemesis, anorexia, and melena.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34018/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Furst DE, Sarkissian E, Blocka K, et al "Serum concentrations of salicylate and naproxen during concurrent therapy in patients with rheumatoid arthritis." Arthritis Rheum 30 (1987): 1157-61[3] Abdel-Rahman M