INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34045, 'Dacomitinib', 'Omeprazole', 'Major', 'Coadministration with proton pump inhibitors (PPIs) may decrease the plasma concentrations of dacomitinib. The proposed mechanism is a pH-dependent reduction in dissolution or absorption of dacomitinib due to prolonged gastric acid suppression induced by PPIs.', NULL, 'Concomitant use of dacomitinib with PPIs should generally be avoided.', NULL, 'Concomitant use of dacomitinib with PPIs should generally be avoided. If acid suppression therapy is required, locally-acting antacids or H2-receptor antagonists may be considered. The manufacturer recommends taking dacomitinib at least 6 hours before or 10 hours after H2-receptor antagonists.', 'Absorption', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Clarithromycin, Vonoprazan, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, Afatinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34098/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Vizimpro (dacomitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34046, 'Danaparoid', 'Acetylsalicylic acid', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', 'Danaparoid, Acenocoumarol, Selexipag, Felodipine, Amlodipine, Propranolol, Metoprolol, Carvedilol, Nebivolol, Nifedipine, Bisoprolol, More', 'Antithrombin III human, Protein C, Bivalirudin, Selexipag, Vorapaxar, Acetylsalicylic acid, Cangrelor', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34099/', '[1] Weale AE, Warwick DJ, Durant N, Prothero D "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 35-7[2] "Product Information. Orgaran (danaparoid)." Organon, West Orange, NJ.[3] "Product Information. Lovenox (enoxaparin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Innohep (tinzaparin)" DuPont Pharmaceuticals, Wilmington, DE.[5] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[6] Walker AM "Predictors of bleeding during heparin therapy." JAMA 244 (1980): 1209-12[7] Klinkhardt U, Breddin HK, Esslinger HU, Haas S, Kalatzis A, Harder S "Interaction between the LMWH reviparin and aspirin in healthy volunteers." Br J Clin Pharmacol 49 (2000): 337-41[8] Heiden D, Rodvien R, Mielke CH "Heparin bleeding, platelet dysfunction, and aspirin." JAMA 246 (1981): 330-1[9] Theroux P, Ouimet H, McCans J, et al. "Aspirin, heparin, or both to treat acute unstable angina." N Engl J Med 319 (1988): 1105-6[10] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 395[11] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn, Kalamazoo, MI.[12] Bang CJ, Riedel B, Talstad I, Berstad A "Interaction between heparin and acetylsalicylic acid on gastric mucosal and skin bleeding in humans." Scand J Gastroenterol 27 (1992): 489-94', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34047, 'Dasatinib', 'Omeprazole', 'Major', 'Nonclinical data indicate that the solubility of dasatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors is likely to reduce dasatinib systemic exposure.', NULL, 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended.', NULL, 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended. If gastric acidity management is necessary, antacids should be considered in place of these agents. However, patients treated with dasatinib should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and two hours after administration of dasatinib.', 'Absorption', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, Afatinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34100/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34048, 'Deferasirox', 'Acetylsalicylic acid', 'Major', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Theoretical concerns exist regarding the potential for increased risk of renal impairment during coadministration of deferasirox with other nephrotoxic agents, including NSAIDs when used chronically or in high dosages.', NULL, 'Caution is advised if deferasirox is used in combination with NSAIDs.', NULL, 'Caution is advised if deferasirox is used in combination with NSAIDs. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, vomiting blood, anorexia, or black, tarry stools. Serum creatinine and/or creatinine clearance should be closely monitored (e.g., prior to initiation of deferasirox therapy, then weekly during the first month after initiation or modification of therapy and monthly thereafter), especially in the elderly and patients with preexisting renal impairment, comorbid conditions, dehydration, or severe infections. Dosage reduction, interruption, or discontinuation should be considered in the presence of creatinine elevations. A progressive increase in serum creatinine beyond the age-appropriate upper limit of normal may warrant an interruption of therapy. Once the creatinine has returned to within the normal range, therapy may be reinitiated at a lower dose followed by a gradual dose escalation according to the product labeling, provided the clinical benefit is expected to outweigh potential risks.', 'Synergism', 'Protein C, Acenocoumarol, Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Atenolol, Ramipril, Valsartan, More', 'Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34101/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Rafat C, Fakhouri F, Ribeil JA, Delarue R, Le Quintrec M "Fanconi Syndrome Due to Deferasirox." Am J Kidney Dis (2009):[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34049, 'Delavirdine', 'Omeprazole', 'Moderate', 'Theoretically, H2 antagonists and proton pump inhibitors may decrease the gastrointestinal absorption of delavirdine. The proposed mechanism is a pH-dependent reduction in drug dissolution or absorption.', NULL, 'While the clinical significance of this potential interaction is unknown, the manufacturer recommends against the chronic use of these drugs with delavirdine due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of delavirdine.', NULL, 'While the clinical significance of this potential interaction is unknown, the manufacturer recommends against the chronic use of these drugs with delavirdine due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of delavirdine.', 'Absorption', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Vonoprazan, Amoxicillin', 'Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Cobicistat, Brincidofovir, Remdesivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34102/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
