INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34078, 'Gefitinib', 'Omeprazole', 'Moderate', 'Coadministration with drugs that cause significant, sustained elevations in gastric pH such as H2-receptor antagonists and proton pump inhibitors may decrease the plasma concentrations of gefitinib. The exact mechanism of interaction is unknown.', NULL, 'The possibility of a diminished therapeutic response to gefitinib should be considered during coadministration with H2-receptor antagonists or proton pump inhibitors.', NULL, 'The possibility of a diminished therapeutic response to gefitinib should be considered during coadministration with H2-receptor antagonists or proton pump inhibitors. Pharmacologic response to gefitinib should be monitored more closely whenever one of these agents is added to or withdrawn from therapy.', 'Absorption', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, Afatinib, More', 'Tinidazole, Amoxicillin, Tetracycline, Bismuth subcitrate potassium, Levofloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34131/', '[1] "Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
