INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34111, 'Omeprazole', 'Mycophenolate mofetil', 'Moderate', 'Coadministration with proton pump inhibitors may reduce the bioavailability of mycophenolic acid (MPA) from the administration of mycophenolate mofetil. The exact mechanism of interaction has not been established, but may involve a decrease in MPA solubility at higher gastric pH levels.', NULL, 'Because clinical relevance of this interaction has not been established, proton pump inhibitors should be used with caution in transplant patients receiving mycophenolate mofetil.', NULL, 'Because clinical relevance of this interaction has not been established, proton pump inhibitors should be used with caution in transplant patients receiving mycophenolate mofetil.', 'Absorption', 'Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Rifabutin, Cimetidine, Nizatidine, Ranitidine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34164/', '[1] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.', 1767369485);
