INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34115, 'Omeprazole', 'Nilotinib', 'Moderate', 'Chronic use of proton pump inhibitors may significantly decrease the oral bioavailability of nilotinib. The mechanism is decreased solubility of nilotinib due to increased gastric pH. Nilotinib''s solubility is pH dependent and it is insoluble at a pH of 4.5 or higher.', NULL, 'The concomitant use of proton pump inhibitors should generally be avoided during therapy with nilotinib.', NULL, 'The concomitant use of proton pump inhibitors should generally be avoided during therapy with nilotinib.', 'Absorption', 'Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, Afatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34168/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
