INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34126, 'Acetylsalicylic acid', 'Pentoxifylline', 'Moderate', 'The use of pentoxifylline has been associated with bleeding and/or prolongation in prothrombin time. Theoretically, the risk of bleeding may be increased by concomitant treatment with anticoagulants or other agents that affect hemostasis such as nonsteroidal anti-inflammatory drugs (NSAIDs), platelet inhibitors, thrombin inhibitors, thrombolytic agents, and dextran. There have been postmarketing reports of increased anticoagulant activity in patients receiving vitamin K antagonists in combination with pentoxifylline.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis. Patients on vitamin K antagonists such as warfarin should have more frequent monitoring of INR following initiation or dosage change of pentoxifylline, while patients receiving other agents that affect hemostasis should have periodic examinations for bleeding including hematocrit and/or hemoglobin.', 'Synergism', 'Cyclandelate, Tolazoline, Phenoxybenzamine, Phentolamine, Isoxsuprine, Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Reslizumab, Dyphylline, More', 'Protein C, Acenocoumarol, Selexipag, Ivabradine, Bempedoic acid, Salicylic acid, Salsalate, Choline salicylate, Ziconotide, Methoxyflurane, Codeine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34179/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34127, 'Pexidartinib', 'Omeprazole', 'Major', 'Coadministration with proton pump inhibitors (PPIs) may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption. The effect of coadministration with an H2-receptor antagonist or locally-acting antacids on pexidartinib pharmacokinetics is unknown.', NULL, 'Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', NULL, 'Concomitant use of pexidartinib with proton pump inhibitors should be avoided. If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist.', 'Absorption', 'Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, Afatinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34180/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34128, 'Acetylsalicylic acid', 'Phenylbutazone', 'Moderate', 'The combined use of low-dose or high-dose aspirin with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation. Aspirin at anti-inflammatory dosages or higher may also decrease the plasma concentrations of many NSAIDs. The decreases have ranged from none or small (piroxicam, meloxicam, naproxen, tolmetin) to substantial (flurbiprofen, ibuprofen). However, the therapeutic response does not appear to be affected.', NULL, 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs.', NULL, 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs. Concomitant administration of NSAIDs is considered contraindicated or not recommended with aspirin at analgesic/anti-inflammatory dosages by many NSAID manufacturers. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as abdominal pain, bloating, sudden dizziness or lightheadedness, nausea, vomiting, hematemesis, anorexia, and melena.', 'Synergism', 'Acetylsalicylic acid, Chondroitin sulfate, Glucosamine, Tolazoline, Dimethyl sulfoxide', 'Clopidogrel, Dicoumarol, Protein C, Acenocoumarol, Selexipag, Anistreplase, Felodipine, Amlodipine, Nifedipine, Amlodipine, Simvastatin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34181/', '[1] Cerner Multum, Inc. "UK Summary of Product Cha