INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34337, 'Avatrombopag', 'Atazanavir', 'Moderate', 'Concomitant use of avatrombopag with strong CYP450 3A4/5 or CYP450 2C9 inhibitors may increase the systemic exposure (AUC) to avatrombopag. The increase in avatrombopag exposure is not expected to have a clinically important effect on platelet counts in patients with chronic liver disease scheduled to undergo a procedure due to the 5-day treatment duration.', NULL, 'No dose adjustment is recommended; however, it may be advisable to monitor platelet count for signs of decreased efficacy of avatrombopag.', NULL, 'No dose adjustment is recommended; however, it may be advisable to monitor platelet count for signs of decreased efficacy of avatrombopag.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Eltrombopag, Romiplostim, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Epinephrine, Coagulation Factor IX Human', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34390/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Doptelet (avatrombopag)." Dova Pharmaceuticals, Durham, NC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34338, 'Axitinib', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of axitinib during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Should concomitant treatment with a potent inhibitor be required, the manufacturer recommends that axitinib dosage be reduced by approximately one-half, as this is predicted to adjust the axitinib systemic exposure (AUC) to the range observed without inhibitors; however, clinical data are lacking. Subsequent dosage adjustments can be made according to individual therapeutic response and tolerability.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34391/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34339, 'Bedaquiline', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34392/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34340, 'Belinostat', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of UGT1A1 may increase the plasma concentrations of belinostat, which is primarily (80% to 90%) metabolized by UGT1A1-mediated glucuronidation.', NULL, 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided.', NULL, 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided. Caution is advised when used with other agents that may also inhibit UGT1A1 such as nilotinib, pazopanib, regorafenib and sorafenib. Patients should be monitored closely for potentially increased adverse effects such as nausea, vomiting, diarrhea, thrombocytopenia, leukopenia (neutropenia and lymphopenia), anemia, infection, and hepatotoxicity.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34393/', '[1] "Product Information. Beleodaq (belinostat)." Spectrum Pharmaceuticals Inc, Irvine, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34341, 'Bepridil', 'Atazanavir', 'Major', 'Coadministration with atazanavir may significantly increase the plasma concentrations of bepridil. The proposed mechanism is atazanavir inhibition of CYP450 3A4, the isoenzyme thought to be responsible for the metabolic clearance of bepridil. Bepridil is associated with dose-related prolongation of the QT interval in addition to the usual range of proarrhythmic effects characteristic of Class I antiarrhythmics, thus high plasma levels may increase the risk of ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', NULL, 'The concomitant use of bepridil and atazanavir is not recommended.', NULL, 'The concomitant use of bepridil and atazanavir is not recommended.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34394/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[3] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[4] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[5] Fuhr U, Maier-Bruggemann A, Blume H, et al. "Grapefruit juice increases oral nimodipine bioavailability." Int J Clin Pharmacol Ther 36 (1998): 126-32[6] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibit